<?php declare(strict_types=1); require __DIR__ . '/lib/functions.php'; $q = $_GET['q'] ?? ''; $recipes = search_recipes($q); $flash = flash_get(); ?> <!doctype html> <html lang="en"> <head> <meta charset="utf-8"> <title>Recipes</title> <meta name="viewport" content="width=device-width, initial-scale=1"> <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet"> <link href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.11.3/font/bootstrap-icons.css" rel="stylesheet"> <link href="lib/theme.css" rel="stylesheet"> </head> <body> <nav class="navbar navbar-expand-lg bg-body-tertiary border-bottom" style="background: var(--surface)"> <div class="container"> <a class="navbar-brand fw-bold" href="index.php">Recipe Box</a> <div class="d-flex"> <a href="new.php" class="btn btn-accent text-white">New Recipe</a> </div> </div> </nav> <main class="container my-4"> <?php if ($flash): ?> <div class="alert alert-<?= htmlspecialchars($flash['type']) ?> mb-4"><?= htmlspecialchars($flash['message']) ?></div> <?php endif; ?> <section class="hero p-4 p-md-5 mb-4"> <div class="d-flex flex-column flex-md-row align-items-start align-items-md-center gap-3"> <div class="flex-grow-1"> <span class="badge rounded-pill">Fresh & tasty</span> <h1 class="mt-2 mb-2 h3">Discover and save your favorite recipes</h1> <div class="muted">Search by title, ingredient, or description</div> </div> <div class="w-100 w-md-auto" style="min-width:320px;"> <form method="get" class=""> <div class="input-group"> <span class="input-group-text"><i class="bi bi-search"></i></span> <input type="text" class="form-control" name="q" placeholder="e.g. chicken, pasta, chocolate" value="<?= htmlspecialchars($q) ?>"> <button class="btn btn-outline-secondary" type="submit">Search</button> </div> </form> </div> </div> </section> <?php if (empty($recipes)): ?> <div class="text-muted">No recipes found.</div> <?php else: ?> <div class="row g-4"> <?php foreach ($recipes as $r): $full = load_recipe($r['slug']); $img = $full['image'] ?? (best_review_photo($full) ?? null); ?> <div class="col-12 col-sm-6 col-lg-4"> <a href="recipe.php?slug=<?= urlencode($r['slug']) ?>" class="text-decoration-none text-reset"> <div class="recipe-card h-100"> <div class="recipe-media"> <?php if ($img): ?> <img src="<?= htmlspecialchars($img) ?>" alt="<?= htmlspecialchars($r['title']) ?>"> <?php endif; ?> <div class="recipe-badge"><i class="bi bi-star-fill rating-star"></i> <?= number_format((float)$r['rating'], 1) ?> <span class="muted">(<?= (int)$r['votes'] ?>)</span></div> </div> <div class="p-3"> <h2 class="h6 mb-1"><?= htmlspecialchars($r['title']) ?></h2> <div class="small muted mb-2"><?= htmlspecialchars(mb_strimwidth($r['description'], 0, 120, 'â€¦')) ?></div> <div class="d-flex align-items-center gap-2 small"> <span class="text-success"><i class="bi bi-egg-fried"></i></span> <span class="muted">Ingredients: <?= count($r['ingredients']) ?></span> </div> </div> </div> </a> </div> <?php endforeach; ?> </div> <?php endif; ?> </main> </body> </html>